mapper.select:

SELECT a.*
FROM (
    SELECT a.*, rownum rn
    FROM (
        SELECT *
        FROM movie_schedule
        WHERE LOWER(schedule_no) LIKE '%' || #{scheduleNo} || '%'
        ORDER BY schedule_startTime DESC
    ) a
    WHERE rownum <= #{endNum}
) a
WHERE rn >= #{startNum}

mapper.update:
UPDATE movie_schedule
SET
screen_restSeat =
screen_restSeat + #{no}
where
schedule_no = #{scheduleNo}

mapper.select:

   SELECT a.*, b.screen_hall,b.screen_floor
FROM movie_schedule a
JOIN screen b ON a.screen_no = b.screen_no
WHERE a.movie_no = #{movieNo}
AND TO_CHAR(a.schedule_date, 'yyyy-mm-dd') = #{time}
AND b.screen_no IN (
    SELECT screen_no 
    FROM screen 
    WHERE cinema_name = (
        SELECT cinema_name 
        FROM cinema 
        WHERE cinema_no = #{cinemaNo}
    )
)
